 ('17','writefiles','','<?php\r\n	$content_qry = mysql_query(\"select * from content\");\r\n	while($content_res = mysql_fetch_array($content_qry)){\r\n		$titel = \"temp/\" .$content_res[\'titel\'] .\".php\";\r\n		$fh = fopen($titel, \"w\");\r\n		$inh = $content_res[\'content\'];	\r\n		if(fwrite($fh, utf8_encode($inh))){\r\n			echo \"$titel wurde erstellt!<br />\";\r\n		}\r\n		fclose($fh);\r\n	}\r\n?>'),
 ('18','readfiles','','<?php\n	$ordner = \"temp/\";\r\n	$dir_handle = opendir($ordner);\n	$str = \"\";\r\n	while ($filename = readdir($dir_handle)){\n		$suffix = substr($filename, -3, 3);\n		$fname = substr($filename, 0, strlen($filename)-4);\n		if ($suffix == \'php\'){\n			echo \"filename: $filename, suffix: $suffix, fname: $fname<br />\";\r\n			if($curfile = file_get_contents(\"temp/\" .$filename, FILE_TEXT)){\n				mysql_query(\"update content set content=\'\" .mysql_real_escape_string($curfile) .\"\' where titel = \'\" .$fname .\"\'\") or die(mysql_error());\n			}\n			else{\n				echo \"Fehler bei der Datei $filename!<br />\";\n			}\r\n		}\r\n	}\n	closedir($dir_handle);\r\n?>'),
 ('21','klassen','','<?php\r\n	global $selbst;\r\n	echo \"<table>\";\r\n	$users_qry = mysql_query(\"select id, name, vorname, nachname, email, ort, telnr, msn, icq, skype, pic from users order by name\");\r\n	while($user = mysql_fetch_array($users_qry)){\r\n		if(file_exists(\'images/avatars/\' .$user[\'id\'] .$user[\'pic\'])){\r\n			$img = \'images/avatars/\' .$user[\'id\'] .$user[\'pic\'];\r\n		}\r\n		else{\r\n			$img = \'images/avatars/default.jpg\';\r\n		}\r\n		echo \"<tr>\";\r\n		echo \'<td><img src=\"\' .$img .\'\" alt=\"Avatar von \' .$user[\'name\'] .\'\" /></td>\';\r\n		echo \'<td>\' .$user[\'vorname\'] .\' \' .$user[\'nachname\'] .\'</td>\';\r\n		echo \'<td><a href=\"\' .$selbst .\'&amp;id=\' .$user[\'id\'] .\'\">mehr Infos...</a></td>\';\r\n		echo \"</tr>\";\r\n	}\r\n	echo \"</table>\";\r\n?>'),
